import model
import networkx
import modelview

class circularListRandomNumberGenerator:
	def __init__(self, l):
		self.l = l
		self.i = 0

	def generate(self):
		num = self.l[self.i]
		self.i = (self.i + 1) % len(self.l)
		return num

def state(node):
	return 'believer' if model.is_believer[node] else 'nonbeliever' if model.is_nonbeliever[node] else 'susceptible'

